output_format.output_interface:

interface PodcastScript {
  segments: Array<{
    speaker: "speaker1" | "speaker2";
    type: "dialogue" | "monologue" | "introduction" | "conclusion";
    content: string;
    pause?: number; // Pausa opcional en segundos después del segmento. Por ejemplo, 0.3, 0.5, 1.2, 2.0, etc.
  }>;
}
